import React from 'react';
import './App.css';
import {fetchHP} from './services/get-data';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
    this.getQuery= this.getQuery.bind(this);
  }
  componentDidMount() {
    this.getData();
  }

  getData () {
    fetchHP ()
    .then(data => {
        const newData = data.map((item, index) => {
          return {...item, newid: index}
        });

        this.setState({
          data: newData
        })
    })
  }

  getQuery (event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }

  render() {
    return (
      <div className="App">
        <h1 className="app__title">Harry Potter Wiki</h1>
        <label htmlFor="search">Busca tu personaje</label>
        <input type="text" onChange={this.getQuery}/>
        <ul className="list">
          {this.state.data
          .filter(mycharac => mycharac.name.toUpperCase().includes(this.state.userInput.toUpperCase()))
          .map(charac => {
            return (
              <li className="character" key={charac.newid}>
                <div className="card">
                  <h2 className="name">{charac.name}</h2>
                  <img className="img" src={charac.image} alt={charac.name}/>
                </div>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default App;
