import React from 'react';
import './App.css';
import {fetchHP} from './services/get-data';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    }
  }
  componentDidMount() {
    this.getData();
  }

  getData () {
    fetchHP ()
    .then(data => {
        const newData = data.map((item, index) => {
          return {...item, newid: index}
        });

        this.setState({
          data: newData
        })
    })
  }

  render() {
    return (
      <div className="App">

      </div>
    );
  }
}

export default App;
