import React from 'react';


const ListCharac = props => {
    const {data, query} = props;
    return (
        <ul className="list">
          {data
          .filter(mycharac => mycharac.name.toUpperCase().includes(this.state.query.toUpperCase()))
          .map(charac => {
            return (
              <li className="character" key={charac.newid}>
                <div className="card">
                  <h2 className="name">{charac.name}</h2>
                  <img className="img" src={charac.image} alt={charac.name}/>
                </div>
              </li>
            );
          })}
        </ul>
    );
};


export default ListCharac;
