import React from 'react';
import './App.css';
import {fetchHP} from './services/get-data';
import ListCharac from './components/ListCharac';
import Filters from './components/Filters';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
    this.getQuery= this.getQuery.bind(this);
  }
  componentDidMount() {
    this.getData();
  }

  getData () {
    fetchHP ()
    .then(data => {
        const newData = data.map((item, index) => {
          return {...item, newid: index}
        });

        this.setState({
          data: newData
        })
    })
  }

  getQuery (event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }

  render() {
    return (
      <div className="App">
        <h1 className="app__title">Harry Potter Wiki</h1>
        <Filters getQuery={this.getQuery}/>
        <ListCharac data={this.state.data} query={this.state.query}/>
      </div>
    );
  }
}

export default App;
