import React from 'react';
import {Link} from 'react-router-dom';


const Detail = props => {
    const {routerProps, data}= props;
    const potterId = parseInt(routerProps.match.params.potterId)
    const character = data.find(item => item.newid === potterId);

    if(character[0]) {
        const {name, image, house, dateOfBirth, gender, patronus}= character[0];

    return (
        <React.Fragment>
            <div className="card">
                  <h2 className="name">{name}</h2>
                  <div className="img__cont">
                    <img className="img" src={image} alt={name}/>
                  </div>
                  <p className="house">{house}</p>
                  <p className="alive">Nacimiento: {dateOfBirth}</p>
                  <p className="gender">{gender}</p>
                  <p>{patronus}</p>
                </div>
                <Link to="/">Volver</Link>
        </React.Fragment>
    );
}else {
    return (
        <React.Fragment>
            <p>No hay m√°s personajes</p>
            <Link to="/">Volver</Link>
        </React.Fragment>
    );
}
};


export default Detail;
