import React from 'react';
import {Link} from 'react-router-dom';


const Detail = props => {
    const {routerProps, data}= props;
    const potterId = parseInt(routerProps.match.params.potterId);
    const character = data.filter(item => item.newid === potterId);
    const gryffindor = 'https://vignette.wikia.nocookie.net/harrypotter/images/b/b1/Gryffindor_ClearBG.png/revision/latest/scale-to-width-down/350?cb=20190222162949';
    const slytherin = 'https://vignette.wikia.nocookie.net/harrypotter/images/0/00/Slytherin_ClearBG.png/revision/latest?cb=20161020182557';
    const hufflepuff = 'https://vignette.wikia.nocookie.net/harrypotter/images/0/06/Hufflepuff_ClearBG.png/revision/latest/scale-to-width-down/350?cb=20161020182518';
    const ravenclaw = 'https://vignette.wikia.nocookie.net/harrypotter/images/4/4e/RavenclawCrest.png/revision/latest/scale-to-width-down/350?cb=20161020182442';
    const hogwarts = 'https://vignette.wikia.nocookie.net/harrypotter/images/a/ae/Hogwartscrest.png/revision/latest/scale-to-width-down/350?cb=20110806202805';

    if(character[0]) {
        const {name, image, house, dateOfBirth, alive, patronus} = character[0];
        return (
            <React.Fragment>
                <div className="card">
                    <h2 className="name">{name}</h2>
                    <img className="img" src={image} alt={name}/>
                    <p className="house">{house}</p>
                    <img src={gryffindor}></img>
                    <p className="birth">Nacimiento: {dateOfBirth ? dateOfBirth : `Desconocido`}</p>
                    <p className="patronus">Patronus: {patronus ? patronus : `Desconocido`}</p>
                    Estado:<i className={alive ? "fas fa-heartbeat" : "fas fa-skull-crossbones"}></i>
                </div>
                <Link to ="/">Volver</Link>
            </React.Fragment>
        );
    } else {
        return (
            <React.Fragment>
                <p>No hay personajes</p>
                <Link to ="/">Volver</Link>
            </React.Fragment>
        );  
    }
};

export default Detail;
