import React from 'react';
import {Link} from 'react-router-dom';


const Detail = props => {
    const {routerprops, data} = props;
    const potterId = parseInt(routerprops.match.params.potterId)
    const character = data.find(item => item.newid === potterId)

    return (
        <React.Fragment>
        <div className="card">
            <h2 className="name">{charac.name}</h2>
            <img className="img" src={charac.image} alt={charac.name}/>
            <p className="birth">{charac.dateOfBirth}</p>
            <p className="alive">Estado: </p>
        </div>
        <Link to="/" >Volver</Link>
        </React.Fragment>
    );
};


export default Detail;
