import React from 'react';
import {Link} from 'react-router-dom';


const Detail = props => {
    const {routerProps, data} = props;
    const potterId = parseInt(routerProps.match.params.potterId)
    const character = data.find(item => item.newid === potterId)

    if (character[0]) {
        const {name, image, dateOfBirth}= character[0];
        return (
            <React.Fragment>
            <div className="card">
                <h2 className="name">{name}</h2>
                <img className="img" src={image} alt={name}/>
                <p className="birth">{dateOfBirth}</p>
                <p className="alive">Estado: </p>
            </div>
            <Link to="/" >Volver</Link>
            </React.Fragment>
        );
